changeSet.sql:
CREATE TEMPORARY TABLE ghg_question_names (post_name VARCHAR(200) PRIMARY KEY);
INSERT INTO ghg_question_names
VALUES
    ('any_benefits'),
    ('postcode_epc'),
    ('english_property'),
    ('own_home');

DELETE FROM wp_postmeta
WHERE post_id IN (
    SELECT ID FROM wp_posts WHERE post_name IN
        (SELECT post_name FROM ghg_question_names)
    );

DELETE FROM wp_posts
WHERE post_name IN (SELECT post_name FROM ghg_question_names);

DROP TEMPORARY TABLE ghg_question_names;
